mapper.select:
select *
from member
where member_id = #{member_id}
and member_pw = #{member_pw}

select TIMESTAMPDIFF(DAY, change_passwd_date, curdate())
from member
where member_id = #{member_id}

mapper.delete:
delete from member
where member_id = #{member_id}
and member_pw = #{member_pw}

mapper.update:
UPDATE member
SET member_pw = #{member_pw}
WHERE member_code = #{member_code}

mapper.select:
select count(*) as cnt from member
where member_id = #{member_id}
and member_pw = #{member_pw}
